import java.util.Scanner;

public class TwoInt {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan=new Scanner (System.in);
		int t=scan.nextInt();
		while(t-->0) {
			int a=scan.nextInt();
			int b=scan.nextInt();
			System.out.println(a+b);
		}

	}

}


